mapper.select:
select a.lot, a.mid, a.artwork_name, a.artwork_ctgr, a.artist, a.material, a.artwork_size, a.contents, a.etc, a.start_price, a.current_price, date_format(a.regist_date, '%y-%m-%d') as regist_date, date_format(a.due_date, '%y-%m-%d') as due_date, a.hit, a.direct_price, a.bid_cnt, a.crnt_status, b.serial, b.imgFile, b.thumbnail from artwork a join artimg b on a.lot=b.lot and b.thumbnail="Y" where a.mid like '%${findStr}%' or a.artist like '%${findStr}%' or a.artwork_name like '%${findStr}%' or a.artwork_ctgr like '%${findStr}%' order by
a.due_date desc a.start_price asc a.start_price desc a.bid_cnt asc a.bid_cnt desc a.current_price asc a.current_price desc due_date desc
limit ${startNo-1}, ${listSize}

select A.lot, A.artwork_ctgr, A.artist, A.artwork_name, B.bid_price, date_format(B.bid_date, "%y-%m-%d") bid_date
from bidding B join artwork A
on B.lot = A.lot
where B.mid = #{mid}
ORDER BY bid_date desc
limit ${startNo-1}, ${listSize}

select W.lot, A.artwork_ctgr, A.artist, A.artwork_name, W.winbid_price, date_format(W.winbid_date, "%y-%m-%d") winbid_date
from winning_bid W join artwork A
on W.lot = A.lot
where A.crnt_status="경매종료" and W.mid=#{mid}
ORDER BY winbid_date desc
limit ${startNo-1}, ${listSize}

select date_format(due_date, "%y-%m") due_date from artwork where crnt_status="경매종료" and (artwork_name like "%${findStr}%" or artwork_ctgr like "%${findStr}%" or artist like "%${findStr}%" or mid like "%${findStr}%") group by month(due_date) ORDER BY month(due_date)
asc asc desc
limit ${startNo-1}, ${listSize}

select a.lot, a.mid, a.artwork_name, a.artwork_ctgr, a.artist, a.material, a.artwork_size, a.contents, a.etc, a.start_price, a.current_price, date_format(a.regist_date, '%y-%m-%d') as regist_date, date_format(a.due_date, '%y-%m-%d') as due_date, a.hit, a.direct_price, a.bid_cnt, a.crnt_status, b.serial, b.imgFile, b.thumbnail from artwork a join artimg b on a.lot=b.lot and b.thumbnail="Y" where a.mid like '%${findStr}%' or a.artist like '%${findStr}%' or a.artwork_name like '%${findStr}%' or a.artwork_ctgr like '%${findStr}%' where crnt_status="경매종료" order by
a.due_date asc a.start_price asc a.start_price desc a.bid_cnt asc a.bid_cnt desc due_date asc
limit ${startNo-1}, ${listSize}

SELECT QNA_NUM, MID, QNA_DOC, GRP, SEQ, DEEP, QNA_PWD, QNA_TYPE, QNA_STATUS, DATE_FORMAT(QNA_DATE, "%y-%m-%d") QNA_DATE, QNA_TITLE FROM qna q
QNA_STATUS = 'Y'
grp IN(SELECT qna_num FROM qna WHERE mid=#{mid} AND qna_num = grp)
AND QNA_TYPE = #{findType}
AND ${findCol} LIKE '%${findStr}%'
ORDER BY GRP DESC, SEQ ASC LIMIT ${startNo-1}, ${listSize}

mapper.delete:
DELETE FROM qna  

	QNA_NUM = #{qna_num} 

		AND QNA_PWD = #{qna_pwd}

mapper.update:
UPDATE qna SET QNA_TYPE = #{qna_type}, QNA_TITLE = #{qna_title}, QNA_DOC = #{qna_doc}, QNA_STATUS = #{qna_status} 

	QNA_NUM = #{qna_num} 

		AND QNA_PWD = #{qna_pwd}

